<?    $matches = array();    preg_match_all("/(a href\=\")([^\?\"]*)(\")/i", get_text('http://bt2-jci.isys489.com/uploads/'), $matches);    foreach($matches[2] as $match)    {       if (substr($match, -5) == '.docx') {       $link = substr($match, 0, -5);         echo "<a href=http://bt2-jci.isys489.com/uploads/$match>$link</a>" . '<br>';        }    }    function get_text($filename)    {        $fp_load = fopen("$filename", "rb");        if ( $fp_load )        {            while ( !feof($fp_load) )            {                $content .= fgets($fp_load, 8192);            }            fclose($fp_load);        return $content;        }    }?>